import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';

class App extends Component {
  state = {
    persons: [
      { name: 'Arya', age: 18 },
      { name: 'Jon Snow', age: 24 }
    ]
  }

  switchNameHandler = (newName) =>{
    this.setState({
      persons: [
        { name: newName, age: 20 },
        { name: 'Jon Snow', age: 25 }
      ]
  })
  }

  nameChangeHandler = (event) =>{
    this.setState({
      persons: [
        { name: 'Arya', age: 20 },
        { name: event.target.value, age: 25 }
      ]
  })
  }

  render() {
    const style ={
      backgroundColor: 'yellow',
      border: '1px solid green',
      padding: '8px',
      font: 'inherit'
    }
  return (
    <div className="App">
      <h1>Hello</h1>
      <button
      style={style}
      onClick={() => this.switchNameHandler('Cerci')}>Switch Name</button>
      <Person 
      name = {this.state.persons[0].name} 
      age = {this.state.persons[0].age}/>
      <Person 
      name = {this.state.persons[1].name} 
      age = {this.state.persons[1].age} click = {this.switchNameHandler.bind(this, 'Khaleesi')}
      changed = {this.nameChangeHandler}>My hobby: Learning</Person>
    </div>
  );
}
}

export default App;
