import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';

class App extends Component {
  state = {
    persons: [
      { name: 'Arya', age: 18 },
      { name: 'Jon Snow', age: 24 }
    ]
  }

  switchNameHandler = (newName) =>{
    this.setState({
      persons: [
        { name: newName, age: 20 },
        { name: 'Jon Snow', age: 25 }
      ]
  })
  }
  render() {
  return (
    <div className="App">
      <h1>Hello darling</h1>
      <button onClick={this.switchNameHandler.bind(this, 'Sansa')}>Switch Name</button>
      <Person 
      name = {this.state.persons[0].name} 
      age = {this.state.persons[0].age}>My hobby: Learning</Person>
      <Person 
      name = {this.state.persons[1].name} 
      age = {this.state.persons[1].age} click = {this.switchNameHandler}>My hobby: Learning</Person>
    </div>
  );
}
}

export default App;
